package com.example.costaricaeducationproject;

import android.annotation.SuppressLint;
import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.cardview.widget.CardView;
import androidx.fragment.app.FragmentTransaction;

import com.bumptech.glide.Glide;

public class ResourcesFragment extends Fragment implements View.OnClickListener {
    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.resources_fragment, container, false);

        Glide.with(this).load("https://www.incimages.com/uploaded_files/image/1920x1080/getty_186744080_2000133320009280342_250241.jpg").into((ImageView) view.findViewById(R.id.downloaded_image));
        Glide.with(this).load("https://www.usnews.com/dims4/USNEWS/218f3a1/2147483647/crop/2120x1414%2B0%2B0/resize/1200x1200%3E/quality/85/?url=http%3A%2F%2Fmedia.beam.usnews.com%2Fef%2F6f%2Fefdc54224a46ad4b82da4dbb3ffa%2F180111-onlinecourse-stock.jpg").into((ImageView) view.findViewById(R.id.online_image));
        Glide.with(this).load("data:image/jpeg;base64,").into((ImageView) view.findViewById(R.id.libraries_image));

        CardView DownloadedButton = (CardView) view.findViewById(R.id.downloaded_card);
        DownloadedButton.setOnClickListener(this);

        CardView OnlineClasses = (CardView) view.findViewById(R.id.online_classes);
        OnlineClasses.setOnClickListener(this);

        CardView OnlineLibraies = (CardView) view.findViewById(R.id.online_libraries);
        OnlineLibraies.setOnClickListener(this);



        return view;
    }

    @SuppressLint("NonConstantResourceId")
    @Override
    public void onClick(View v) {

        switch (v.getId()) {
            case R.id.downloaded_card:
                assert getFragmentManager() != null;
                final FragmentTransaction ft = getFragmentManager().beginTransaction();
                ft.replace(R.id.fragment_container, new DownloadedFragment(), "NewFragmentTag");
                ft.commit();
                break;

            case R.id.online_classes:
                startActivity(new Intent(Intent.ACTION_VIEW, Uri.parse("https://www.khanacademy.org/")));
                break;

            case R.id.online_libraries:
                startActivity(new Intent(Intent.ACTION_VIEW, Uri.parse("https://en.wikipedia.org/wiki/List_of_digital_library_projects")));
                break;

            default:
                break;
        }

    }




}
